def first_neg(s: str) -> int:
    """returns the first character (one-indexed) that causes Santa to enter basement, if it exists, else None"""
    floor = 0
    for i, char in enumerate(s, start=1):
        if char == '(':
            floor += 1
        elif char == ')':
            floor -= 1
        if floor <0:
            return i
        
print(first_neg(input))