INPUT = """




"""


def move(current, direction) -> list[int]:
    current_ = current.copy()
    if direction == "D":
        current_[0] += 1
    elif direction == "U":
        current_[0] -= 1
    elif direction == "L":
        current_[1] -= 1
    else:
        current_[1] += 1
    return current_


current_spot = [1, 1]
pad = [[1, 2, 3], [4, 5, 6], [7, 8, 9]]

code = ""
for row in INPUT.split():
    for char in row:
        new_spot = move(current_spot.copy(), char)
        if new_spot[0] in range(3) and new_spot[1] in range(3):
            current_spot = new_spot
    code += str(pad[current_spot[0]][current_spot[1]])

assert len(code) == len(INPUT.split())
print(code)

# part 2
pad = [
    [0, 0, 1, 0, 0],
    [0, 2, 3, 4, 0],
    [5, 6, 7, 8, 9],
    [0, "A", "B", "C", 0],
    [0, 0, "D", 0, 0],
]
current_spot = [2, 0]
acceptable_locations = [[i, j] for i in range(5) for j in range(5) if pad[i][j]]
code = ""
for row in INPUT.split():
    for char in row:
        new_spot = move(current_spot.copy(), char)
        if new_spot in acceptable_locations:
            current_spot = new_spot
    code += str(pad[current_spot[0]][current_spot[1]])

assert len(code) == len(INPUT.split())
print(code)
